import { useState } from "react";
import { root } from '.';
import { useEffect } from "react";
import CreateUsersPage from "./components/CreateUsersPage";
import UserEditorWindow from "./components/UI/modals/UserEditorWindow.jsx";
import { TEST_URL } from './MAIN.js';
import { GET_USERS_URL } from './MAIN.js';
import { DELETE_USERS_URL } from './MAIN.js';

function Users() {
    const [errors, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [GettedUsers, setGettedUsers] = useState([]);

    const [userItems, setUserItems] = useState(GettedUsers)

    function GetUsers() {

        useEffect(() => {
            fetch(GET_USERS_URL)
                .then(response => response.json())
                .then(
                    (result) => {
                        setIsLoaded(true);
                        setUserItems(result);
                    },
                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                        console.log(errors);
                        console.log(isLoaded);
                    }
                )
        }, [])
        return GettedUsers;
    };
    GetUsers();

    const deleteUser = (item) => {
        fetch((DELETE_USERS_URL + item.id), {
            method: 'DELETE'
        });

        setUserItems(userItems.filter(i => i.id !== item.id))
    };

    const editUser = (item) => {
        console.log(item);
        root.render (<UserEditorWindow id={item.id} name={item.name} email={item.email} phone={item.phone} password={item.password}/>)       
    };

    return (
        <CreateUsersPage editUser={editUser} deleteUser={deleteUser} userItems={userItems} />
    );  
};

export default Users;
